import { Button } from "@/components/ui/button";
import { ShoppingCart, User } from "lucide-react";
import { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { cartStore } from "@/lib/cart-store";
import { getCurrentUser, logoutUser } from "@/lib/api";

interface LayoutProps {
  children: React.ReactNode;
}

export function Layout({ children }: LayoutProps) {
  const [cartCount, setCartCount] = useState(0);
  const [user, setUser] = useState<any | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    const updateCartCount = () => {
      setCartCount(cartStore.getCount());
    };

    updateCartCount();
    const unsubscribe = cartStore.subscribe(updateCartCount);

    const handleUserLogin = () => setTimeout(updateCartCount, 200);
    const handleUserLogout = () => updateCartCount();
    const handleCartUpdated = () => updateCartCount();

    window.addEventListener("userLogin", handleUserLogin);
    window.addEventListener("userLogout", handleUserLogout);
    window.addEventListener("cartUpdated", handleCartUpdated);

    return () => {
      unsubscribe();
      window.removeEventListener("userLogin", handleUserLogin);
      window.removeEventListener("userLogout", handleUserLogout);
      window.removeEventListener("cartUpdated", handleCartUpdated);
    };
  }, []);

  useEffect(() => {
    const updateUser = () => {
      const currentUser = getCurrentUser();
      setUser(currentUser);
      setIsLoading(false);
    };

    updateUser();

    const handleStorageChange = (e: StorageEvent) => {
      if (e.key === "user") updateUser();
    };

    window.addEventListener("storage", handleStorageChange);

    return () => window.removeEventListener("storage", handleStorageChange);
  }, [location.pathname]);

  const isActive = (path: string) => location.pathname === path;

  const handleLogout = async () => {
    try {
      await logoutUser();
      setUser(null);
      window.location.href = "/login";
    } catch (error) {
      console.error("Error al cerrar sesin:", error);
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-50 bg-card/95 backdrop-blur supports-[backdrop-filter]:bg-card/60 border-b border-border">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <Link to="/" className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-ceramics rounded-lg flex items-center justify-center">
                <span className="text-ceramics-foreground font-bold">B</span>
              </div>
              <span className="text-xl font-bold text-foreground">Barroco Ceramics</span>
            </Link>
            <nav className="hidden md:flex items-center space-x-6">
              <Link
                to="/"
                className={`text-sm font-medium transition-colors hover:text-primary ${
                  isActive("/") ? "text-primary" : "text-muted-foreground"
                }`}
              >
                Inicio
              </Link>

              <Link
                to="/productos"
                className={`text-sm font-medium transition-colors hover:text-primary ${
                  isActive("/productos") ? "text-primary" : "text-muted-foreground"
                }`}
              >
                Productos
              </Link>
            </nav>

            <div className="flex items-center space-x-4">
              <Link to="/carrito">
                <Button variant="ghost" size="icon" className="relative">
                  <ShoppingCart className="h-4 w-4" />
                  {cartCount > 0 && (
                    <span className="absolute -top-1 -right-1 bg-ceramics text-ceramics-foreground text-xs rounded-full h-5 w-5 flex items-center justify-center">
                      {cartCount}
                    </span>
                  )}
                </Button>
              </Link>
              {!isLoading &&
                (user ? (
                  <div className="flex items-center space-x-2">
                    <Link to="/perfil" className="text-sm font-medium text-foreground">
                      Hola, {user.firstName || user.email}
                    </Link>

                    <Button variant="outline" size="sm" onClick={handleLogout}>
                      Salir
                    </Button>
                  </div>
                ) : (
                  <Link to="/login">
                    <Button variant="ghost" size="icon">
                      <User className="h-4 w-4" />
                    </Button>
                  </Link>
                ))}
            </div>
          </div>
        </div>
      </header>

      <main className="flex-1">{children}</main>
      <footer className="bg-muted mt-16">
        <div className="container mx-auto px-4 py-12">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
            <div>
              <h3 className="font-semibold text-foreground mb-4">Sobre Nosotros</h3>
              <p className="text-muted-foreground text-sm leading-relaxed">
                Creamos cermicas artesanales personalizadas con amor y dedicacin. Cada pieza
                es nica y especial.
              </p>
            </div>

            <div>
              <h3 className="font-semibold text-foreground mb-4">Enlaces Rpidos</h3>
              <ul className="space-y-2 text-sm">
                <li>
                  <Link to="/productos" className="text-muted-foreground hover:text-primary">
                    Productos
                  </Link>
                </li>
                <li>
                  <Link to="/personalizar" className="text-muted-foreground hover:text-primary">
                    Personalizar
                  </Link>
                </li>
                <li>
                  <Link to="/sobre-nosotros" className="text-muted-foreground hover:text-primary">
                    Sobre Nosotros
                  </Link>
                </li>
                <li>
                  <Link to="/contacto" className="text-muted-foreground hover:text-primary">
                    Contacto
                  </Link>
                </li>
              </ul>
            </div>

            <div>
              <h3 className="font-semibold text-foreground mb-4">Atencin al Cliente</h3>
              <ul className="space-y-2 text-sm">
                <li>
                  <Link to="/ayuda" className="text-muted-foreground hover:text-primary">
                    Centro de Ayuda
                  </Link>
                </li>
                <li>
                  <Link to="/envios" className="text-muted-foreground hover:text-primary">
                    Envos
                  </Link>
                </li>
                <li>
                  <Link to="/devoluciones" className="text-muted-foreground hover:text-primary">
                    Devoluciones
                  </Link>
                </li>
                <li>
                  <Link to="/politicas" className="text-muted-foreground hover:text-primary">
                    Polticas
                  </Link>
                </li>
              </ul>
            </div>

            <div>
              <h3 className="font-semibold text-foreground mb-4">Contacto</h3>
              <div className="space-y-2 text-sm text-muted-foreground">
                <p>Email: info@barrococeramics.com</p>
                <p>Telfono: +1 234 567 8900</p>
                <p>Horario: Lun-Vie 9:00-18:00</p>
              </div>
            </div>
          </div>

          <div className="border-t border-border mt-8 pt-8">
            <div className="flex flex-col md:flex-row justify-between items-center">
