import { useState, useEffect } from "react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { productAPI } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";

export default function RegisterProductSection() {
  const [form, setForm] = useState({
    idProduct: "",  
    name: "",
    price: "",
    stock: "",
    description: "",
    categoryId: "",
    url: "",
  });
  const [products, setProducts] = useState([]);
  const [editId, setEditId] = useState<string | null>(null);
  const [editForm, setEditForm] = useState({
    idProduct: "",
    name: "",
    price: "",
    stock: "",
    description: "",
    categoryId: "",
    url: "",
  });
  const { toast } = useToast();

  const fetchProducts = () => productAPI.getAll().then(setProducts);

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!form.name || !form.price || !form.stock || !form.url) {
      toast({ title: "Faltan campos obligatorios", variant: "destructive" });
      return;
    }
    const productData = {
      idProduct: form.idProduct, 
      name: form.name,
      price: parseFloat(form.price), 
      stock: parseInt(form.stock), 
      description: form.description || "", 
      categoryId: form.categoryId || null, 
      url: form.url, 
    };

    console.log("Datos a enviar para registrar producto:", productData);

    try {
      await productAPI.create(productData); 
      toast({ title: "Producto registrado" });
      setForm({ idProduct: "", name: "", price: "", stock: "", description: "", categoryId: "", url: "" }); 
      fetchProducts(); 
    } catch (err: any) {
      console.error("Error al registrar producto:", err);
      toast({ title: "Error al registrar", description: err.message, variant: "destructive" });
    }
  };

  const handleEdit = (p: any) => {
    setEditId(p.id ?? p._id);
    setEditForm({
      idProduct: p.idProduct ?? "",
      name: p.name,
      price: p.price,
      stock: p.stock,
      description: p.description ?? "",
      categoryId: p.categoryId ?? "",
      url: p.url ?? "",
    });
  };

  const handleEditSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editId) return;

    const updatedProduct = {
      idProduct: editForm.idProduct, 
      name: editForm.name,
      price: parseFloat(editForm.price),
      stock: parseInt(editForm.stock),
      description: editForm.description,
      categoryId: editForm.categoryId,
      url: editForm.url,
    };

    try {
      await productAPI.update(editId, updatedProduct); 
      toast({ title: "Producto actualizado" });
      setEditId(null);
      fetchProducts(); 
    } catch (err: any) {
      console.error("Error al actualizar el producto:", err);
      toast({ title: "Error al actualizar", description: err.message, variant: "destructive" });
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await productAPI.delete(id);
      toast({ title: "Producto eliminado" });
      fetchProducts();
    } catch (err: any) {
      toast({ title: "Error al eliminar", description: err.message, variant: "destructive" });
    }
  };

  return (
    <div>
      <h2 className="font-bold mb-4">Registrar/Editar Producto</h2>
      <Separator />
      <form className="mt-4 mb-6" onSubmit={handleSubmit}>
        <Input
          placeholder="ID Producto (opcional)"
          className="mb-2"
          value={form.idProduct}
          onChange={(e) => setForm({ ...form, idProduct: e.target.value })}
        />
        <Input
          placeholder="Nombre"
          className="mb-2"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
        />
        <Input
          placeholder="Precio"
          type="number"
          className="mb-2"
          value={form.price}
          onChange={(e) => setForm({ ...form, price: e.target.value })}
        />
        <Input
          placeholder="Stock"
          type="number"
          className="mb-2"
          value={form.stock}
          onChange={(e) => setForm({ ...form, stock: e.target.value })}
        />
        <Input
          placeholder="Descripcin"
          className="mb-2"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
        />
        <Input
          placeholder="ID Categora"
          className="mb-2"
          value={form.categoryId}
          onChange={(e) => setForm({ ...form, categoryId: e.target.value })}
        />
        <Input
          placeholder="URL de la imagen"
          className="mb-2"
          value={form.url}
          onChange={(e) => setForm({ ...form, url: e.target.value })}
        />
        <Button type="submit">Registrar</Button>
      </form>

      {editId && (
        <form className="mb-6" onSubmit={handleEditSubmit}>
          <h3 className="font-semibold mb-2">Editar Producto</h3>
          <Input
            placeholder="ID Producto (opcional)"
            className="mb-2"
            value={editForm.idProduct}
            onChange={(e) => setEditForm({ ...editForm, idProduct: e.target.value })}
          />
          <Input
            placeholder="Nombre"
            className="mb-2"
            value={editForm.name}
            onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
          />
          <Input
            placeholder="Precio"
            type="number"
            className="mb-2"
            value={editForm.price}
            onChange={(e) => setEditForm({ ...editForm, price: e.target.value })}
          />
          <Input
            placeholder="Stock"
            type="number"
            className="mb-2"
            value={editForm.stock}
            onChange={(e) => setEditForm({ ...editForm, stock: e.target.value })}
          />
          <Input
            placeholder="Descripcin"
            className="mb-2"
            value={editForm.description}
            onChange={(e) => setEditForm({ ...editForm, description: e.target.value })}
          />
          <Input
            placeholder="ID Categora"
            className="mb-2"
            value={editForm.categoryId}
            onChange={(e) => setEditForm({ ...editForm, categoryId: e.target.value })}
          />
          <Input
            placeholder="URL de la imagen"
            className="mb-2"
            value={editForm.url}
            onChange={(e) => setEditForm({ ...editForm, url: e.target.value })}
          />
          <Button type="submit">Guardar cambios</Button>
          <Button variant="ghost" type="button" onClick={() => setEditId(null)}>
            Cancelar
          </Button>
        </form>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
        {products.map((p: any, idx: number) => (
          <Card key={p.id ?? p._id ?? idx}>
            <CardHeader>
              <CardTitle>{p.name}</CardTitle>
            </CardHeader>
            <CardContent>
              <div>Precio: ${p.price}</div>
              <div>Stock: {p.stock}</div>
              <div>
                URL:{" "}
                <a href={p.url} target="_blank" rel="noopener noreferrer">
                  Ver imagen
                </a>
              </div>
              <Button size="sm" className="mr-2" onClick={() => handleEdit(p)}>
                Editar
              </Button>
              <Button size="sm" variant="destructive" onClick={() => handleDelete(p.id ?? p._id)}>
                Eliminar
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Separator } from "@/components/ui/separator";
import { productAPI } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";

export default function StockSection() {
  const [products, setProducts] = useState([]);
  const [editId, setEditId] = useState<string | null>(null);
  const [editStock, setEditStock] = useState<number>(0);
  const { toast } = useToast();

  const fetchProducts = () => productAPI.getAll().then(setProducts);

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleEdit = (p: any) => {
    setEditId(p.id ?? p._id);
    setEditStock(Number(p.stock));
  };

  const handleEditSubmit = async (e: React.FormEvent) => {
  try {
    e.preventDefault();
    if (!editId) return;
    await productAPI.update(editId, { stock: editStock });
    toast({ title: "Stock actualizado" });
    setEditId(null);
    fetchProducts();
  }catch (error) {
   console.error("Error al actualizar stock");
  }};

  return (
    <div>
      <h2 className="font-bold mb-4">CRUD Stock</h2>
      <Separator />
      {editId && (
        <form className="mb-6" onSubmit={handleEditSubmit}>
          <h3 className="font-semibold mb-2">Editar Stock</h3>
          <Input
            placeholder="Stock"
            type="number"
            className="mb-2"
            value={editStock}
            onChange={e => setEditStock(Number(e.target.value))}
          />
          <Button type="submit">Guardar cambios</Button>
          <Button variant="ghost" type="button" onClick={() => setEditId(null)}>Cancelar</Button>
        </form>
      )}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
        {products.map((p: any, idx: number) => (
          <Card key={p.id ?? p._id ?? idx}>
            <CardHeader>
              <CardTitle>{p.name}</CardTitle>
            </CardHeader>
            <CardContent>
              <div>Stock: {p.stock}</div>
              <Button size="sm" onClick={() => handleEdit(p)}>Editar Stock</Button>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

import { useState, useEffect } from "react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { customerAPI } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";

export default function UsersSection() {
  const [users, setUsers] = useState<any[]>([]);
  const [formVisible, setFormVisible] = useState(false);
  const [form, setForm] = useState({
    role: "customer",
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    password: "",
    billingAddress: "",
    shippingAddress: "",
    idCustomer: "",
  });
  const [editMongoId, setEditMongoId] = useState<string | null>(null);
  const [editForm, setEditForm] = useState<any>({
    role: "customer",
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    password: "",
    billingAddress: "",
    shippingAddress: "",
    idCustomer: "",
  });
  const { toast } = useToast();

  const fetchUsers = async () => {
    try {
      const data = await customerAPI.getAll() as any[];
      setUsers(
        data.map((u: any) => ({
          mongoId: u._id, // ID real de Mongo
          idCustomer: u.idCustomer,
          role: u.role,
          firstName: u.firstName,
          lastName: u.lastName,
          email: u.email,
          phone: u.phone,
          billingAddress: u.billingAddress,
          shippingAddress: u.shippingAddress,
        }))
      );
    } catch (error) {
      toast({ title: "Error al obtener usuarios", variant: "destructive" });
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await customerAPI.create({ ...form, createdAt: new Date(), updatedAt: new Date() });
      toast({ title: "Usuario registrado" });
      setForm({
        role: "customer",
        firstName: "",
        lastName: "",
        email: "",
        phone: "",
        password: "",
        billingAddress: "",
        shippingAddress: "",
        idCustomer: "",
      });
      setFormVisible(false);
      fetchUsers();
    } catch (error) {
      toast({ title: "Error al registrar usuario", variant: "destructive" });
    }
  };

  const handleDelete = async (mongoId: string) => {
    try {
      await customerAPI.delete(mongoId); // Usar _id aqu
      toast({ title: "Usuario eliminado" });
      fetchUsers();
    } catch (error) {
      toast({ title: "Error al eliminar usuario", variant: "destructive" });
    }
  };

  const handleEdit = (u: any) => {
    setEditMongoId(u.mongoId); // Guardamos _id para la edicin
    setEditForm({
      role: u.role,
      firstName: u.firstName,
      lastName: u.lastName,
      email: u.email,
      phone: u.phone,
      password: "",
      billingAddress: u.billingAddress,
      shippingAddress: u.shippingAddress,
      idCustomer: u.idCustomer,
    });
  };

  const handleEditSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editMongoId) return;

    const dataToSend: any = {};
    Object.entries(editForm).forEach(([key, value]) => {
      if (value !== "") {
        dataToSend[key] = value;
      }
    });
    dataToSend.updatedAt = new Date();

    try {
      await customerAPI.update(editMongoId, dataToSend); // Usar _id aqu
      toast({ title: "Usuario actualizado" });
      setEditMongoId(null);
      fetchUsers();
    } catch (error) {
      toast({ title: "Error al actualizar usuario", variant: "destructive" });
    }
  };

  return (
    <div>
      <h2 className="font-bold mb-4">CRUD Usuarios</h2>
      <Separator />

      <Button className="mt-4 mb-4" onClick={() => setFormVisible(!formVisible)}>
        {formVisible ? "Ocultar formulario" : "Crear nuevo cliente"}
      </Button>

      {formVisible && (
        <form className="mt-4 mb-6" onSubmit={handleSubmit}>
          <Input placeholder="Nombre" className="mb-2" value={form.firstName} onChange={(e) => setForm({ ...form, firstName: e.target.value })} />
          <Input placeholder="Apellido" className="mb-2" value={form.lastName} onChange={(e) => setForm({ ...form, lastName: e.target.value })} />
          <Input placeholder="Email" className="mb-2" value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} />
          <Input placeholder="Telfono" className="mb-2" value={form.phone} onChange={(e) => setForm({ ...form, phone: e.target.value })} />
          <Input placeholder="Contrasea" type="password" className="mb-2" value={form.password} onChange={(e) => setForm({ ...form, password: e.target.value })} />
          <Input placeholder="Direccin Facturacin" className="mb-2" value={form.billingAddress} onChange={(e) => setForm({ ...form, billingAddress: e.target.value })} />
          <Input placeholder="Direccin Envo" className="mb-2" value={form.shippingAddress} onChange={(e) => setForm({ ...form, shippingAddress: e.target.value })} />
          <Input placeholder="ID Cliente" className="mb-2" value={form.idCustomer} onChange={(e) => setForm({ ...form, idCustomer: e.target.value })} />
          <Button type="submit">Registrar</Button>
        </form>
      )}

      {editMongoId && (
        <form className="mb-6" onSubmit={handleEditSubmit}>
          <h3 className="font-semibold mb-2">Editar Usuario</h3>
          <Input placeholder="Nombre" className="mb-2" value={editForm.firstName} onChange={(e) => setEditForm({ ...editForm, firstName: e.target.value })} />
          <Input placeholder="Apellido" className="mb-2" value={editForm.lastName} onChange={(e) => setEditForm({ ...editForm, lastName: e.target.value })} />
          <Input placeholder="Email" className="mb-2" value={editForm.email} onChange={(e) => setEditForm({ ...editForm, email: e.target.value })} />
          <Input placeholder="Telfono" className="mb-2" value={editForm.phone} onChange={(e) => setEditForm({ ...editForm, phone: e.target.value })} />
          <Input placeholder="Contrasea (vaco = no cambia)" type="password" className="mb-2" value={editForm.password} onChange={(e) => setEditForm({ ...editForm, password: e.target.value })} />
          <Input placeholder="Direccin Facturacin" className="mb-2" value={editForm.billingAddress} onChange={(e) => setEditForm({ ...editForm, billingAddress: e.target.value })} />
          <Input placeholder="Direccin Envo" className="mb-2" value={editForm.shippingAddress} onChange={(e) => setEditForm({ ...editForm, shippingAddress: e.target.value })} />
          <Input placeholder="ID Cliente" className="mb-2" value={editForm.idCustomer} onChange={(e) => setEditForm({ ...editForm, idCustomer: e.target.value })} />
          <Input placeholder="Rol" className="mb-2" value={editForm.role} onChange={(e) => setEditForm({ ...editForm, role: e.target.value })} />
          <Button type="submit">Guardar cambios</Button>
          <Button variant="ghost" type="button" onClick={() => setEditMongoId(null)}>Cancelar</Button>
        </form>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
        {users.map((u, idx) => (
          <Card key={u.mongoId ?? idx}>
            <CardHeader>
              <CardTitle>{u.firstName} {u.lastName}</CardTitle>
              <div className="text-sm text-gray-500">ID Cliente: {u.idCustomer}</div>
            </CardHeader>
            <CardContent>
              <div>Email: {u.email}</div>
              <div>Telfono: {u.phone}</div>
              <Button size="sm" className="mr-2" onClick={() => handleEdit(u)}>Editar</Button>
              <Button size="sm" variant="destructive" onClick={() => handleDelete(u.mongoId)}>Eliminar</Button>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

