export default function Auth() {
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const [loginForm, setLoginForm] = useState({
    email: "",
    password: "",
  });

  const [registerForm, setRegisterForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
    phone: "",
    billingAddress: "",
    shippingAddress: "",
  });

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const user = await LoginAPI.login(loginForm.email, loginForm.password);

      toast({
        title: "Inicio de sesin exitoso",
        description: `Bienvenido, ${user.firstName || user.email}`,
      });

      window.location.href = "/"

    } catch (error: any) {
      toast({
        title: "Error de autenticacin",
        description: error.message || "No se pudo iniciar sesin",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();

    if (registerForm.password !== registerForm.confirmPassword) {
      toast({
        title: "Error",
        description: "Las contraseas no coinciden",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const customerData = {
        firstName: registerForm.firstName,
        lastName: registerForm.lastName,
        email: registerForm.email,
        password: registerForm.password,
        phone: registerForm.phone,
        billingAddress: registerForm.billingAddress,
        shippingAddress: registerForm.shippingAddress,
      };

      await RegisterAPI.registerCustomer(customerData);

      toast({
        title: "Registro exitoso",
        description: "Tu cuenta ha sido creada correctamente. Redirigiendo...",
      });

      setRegisterForm({
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        confirmPassword: "",
        phone: "",
        billingAddress: "",
        shippingAddress: "",
      });

      setTimeout(() => {
        window.location.href = "/";
      }, 1000);

    } catch (error) {
      toast({
        title: "Error en el registro",
        description: "No se pudo crear la cuenta. Intenta nuevamente.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-cornsilk via-warm to-accent flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        <div className="text-center mb-8">
          <Link to="/" className="inline-flex items-center space-x-2">
            <div className="w-10 h-10 bg-ceramics rounded-lg flex items-center justify-center">
              <span className="text-ceramics-foreground font-bold text-lg">B</span>
            </div>
            <span className="text-2xl font-bold text-foreground">Barroco Ceramics</span>
          </Link>
        </div>

        <Card className="bg-card/95 backdrop-blur supports-[backdrop-filter]:bg-card/90">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl">Bienvenido</CardTitle>
            <CardDescription>
              Accede a tu cuenta o crate una nueva para comenzar
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Tabs defaultValue="login" className="w-full">
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="login">Iniciar Sesin</TabsTrigger>
                <TabsTrigger value="register">Registrarse</TabsTrigger>
              </TabsList>

              <TabsContent value="login" className="space-y-4">
                <form onSubmit={handleLogin} className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="login-email">Email</Label>
                    <Input
                      id="login-email"
                      type="email"
                      placeholder="tu@email.com"
                      value={loginForm.email}
                      onChange={(e) => setLoginForm(prev => ({ ...prev, email: e.target.value }))}
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="login-password">Contrasea</Label>
                    <Input
                      id="login-password"
                      type="password"
                      value={loginForm.password}
                      onChange={(e) => setLoginForm(prev => ({ ...prev, password: e.target.value }))}
                      required
                    />
                  </div>
                  <Button
                    type="submit"
                    className="w-full bg-ceramics hover:bg-ceramics/90 text-ceramics-foreground"
                    disabled={isLoading}
                  >
                    {isLoading ? "Iniciando sesin..." : "Iniciar Sesin"}
                  </Button>
                </form>

                <div className="text-center">
                  <Button variant="link" className="text-sm text-muted-foreground">
                    Olvidaste tu contrasea?
                  </Button>
                </div>
              </TabsContent>

              <TabsContent value="register" className="space-y-4">
                <form onSubmit={handleRegister} className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="firstName">Nombre</Label>
                      <Input
                        id="firstName"
                        value={registerForm.firstName}
                        onChange={(e) => setRegisterForm(prev => ({ ...prev, firstName: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="lastName">Apellido</Label>
                      <Input
                        id="lastName"
                        value={registerForm.lastName}
                        onChange={(e) => setRegisterForm(prev => ({ ...prev, lastName: e.target.value }))}
                        required
                      />
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="register-email">Email</Label>
                    <Input
                      id="register-email"
                      type="email"
                      placeholder="tu@email.com"
                      value={registerForm.email}
                      onChange={(e) => setRegisterForm(prev => ({ ...prev, email: e.target.value }))}
                      required
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="phone">Telfono</Label>
                    <Input
                      id="phone"
                      type="tel"
                      placeholder="+1 234 567 8900"
                      value={registerForm.phone}
                      onChange={(e) => setRegisterForm(prev => ({ ...prev, phone: e.target.value }))}
                      required
                    />
                  </div>

                  <div className="grid grid-cols-1 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="register-password">Contrasea</Label>
                      <Input
                        id="register-password"
                        type="password"
                        value={registerForm.password}
                        onChange={(e) => setRegisterForm(prev => ({ ...prev, password: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="confirm-password">Confirmar Contrasea</Label>
                      <Input
                        id="confirm-password"
                        type="password"
                        value={registerForm.confirmPassword}
                        onChange={(e) => setRegisterForm(prev => ({ ...prev, confirmPassword: e.target.value }))}
                        required
                      />
                    </div>
                  </div>

                  <Separator />

                  <div className="space-y-2">
                    <Label htmlFor="billingAddress">Direccin de Facturacin</Label>
                    <Input
                      id="billingAddress"
                      placeholder="Calle Principal 123, Ciudad"
                      value={registerForm.billingAddress}
                      onChange={(e) => setRegisterForm(prev => ({ ...prev, billingAddress: e.target.value }))}
                      required
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="shippingAddress">Direccin de Envo</Label>
                    <Input
                      id="shippingAddress"
                      placeholder="Misma que facturacin o diferente"
                      value={registerForm.shippingAddress}
                      onChange={(e) => setRegisterForm(prev => ({ ...prev, shippingAddress: e.target.value }))}
                    />
                  </div>

                  <Button
                    type="submit"
                    className="w-full bg-ceramics hover:bg-ceramics/90 text-ceramics-foreground"
                    disabled={isLoading}
                  >
                    {isLoading ? "Creando cuenta..." : "Crear Cuenta"}
                  </Button>
                </form>
              </TabsContent>
            </Tabs>

            <div className="mt-6">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <Separator />
                </div>
                <div className="relative flex justify-center text-xs uppercase">
                  <span className="bg-card px-2 text-muted-foreground">O</span>
                </div>
              </div>

              <div className="mt-4">
                <Link to="/" className="block">
                  <Button variant="outline" className="w-full">
                    Continuar como Invitado
                  </Button>
                </Link>
              </div>
            </div>
          </CardContent>
        </Card>

        <div className="text-center mt-6">
          <p className="text-sm text-muted-foreground">
            Al registrarte, aceptas nuestros{" "}
            <Link to="/terminos" className="text-ceramics hover:underline">
              Trminos de Servicio
            </Link>{" "}
            y{" "}
            <Link to="/privacidad" className="text-ceramics hover:underline">
              Poltica de Privacidad
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
}

import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Star, Search, Filter, ShoppingCart, Heart } from "lucide-react";
import { productAPI, categoryAPI, getCurrentUser } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import { cartStore } from "@/lib/cart-store";
import { ensureCartExists, upsertCartForCurrentUser } from "@/lib/cart-sync";

// ... (imports y hooks igual que antes)

export default function Products() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const [sortBy, setSortBy] = useState<string>("name");
  const [isAddingToCart, setIsAddingToCart] = useState<string | null>(null);
  const { toast } = useToast();

  const { data: products, isLoading: productsLoading } = useQuery({
    queryKey: ["products"],
    queryFn: productAPI.getAll,
  });

  const { data: categories } = useQuery({
    queryKey: ["categories"],
    queryFn: categoryAPI.getAll,
  });

  const productList = Array.isArray(products) ? products : [];
  const categoryList = Array.isArray(categories) ? categories : [];

  // ---------------- FILTRO ----------------
  const filteredProducts = productList
    .filter((product: any) => {
      const matchesSearch =
        product.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description?.toLowerCase().includes(searchTerm.toLowerCase());

      const matchesCategory =
        selectedCategory === "all" ||
        String(product.category) === selectedCategory; // aqu conectamos product.category con category._id

      return matchesSearch && matchesCategory;
    })
    .sort((a: any, b: any) => {
      switch (sortBy) {
        case "price-low":
          return (a.price || 0) - (b.price || 0);
        case "price-high":
          return (b.price || 0) - (a.price || 0);
        case "name":
        default:
          return (a.name || "").localeCompare(b.name || "");
      }
    });

  // ---------------- FUNCIONES addToCart y wishlist ----------------
  const addToCart = async (product: any) => {
    const user = getCurrentUser();
    if (!user || user.role !== "customer") {
      toast({
        title: "Error",
        description: "Debes iniciar sesin como cliente para agregar productos al carrito",
        variant: "destructive",
      });
      return;
    }

    const productId = product.idProduct || `temp-${Date.now()}`;
    setIsAddingToCart(productId);

    try {
      const cartId = await ensureCartExists();
      if (!cartId) throw new Error("No se pudo crear o encontrar carrito");

      const cartItem = {
        id: `${productId}-${Date.now()}`,
        productId: productId,
        name: product.name || "Producto",
        price: product.price,
        quantity: 1,
      };

      cartStore.addItem(cartItem);
      await upsertCartForCurrentUser();
      sessionStorage.setItem("cart", JSON.stringify(cartStore.getItems()));

      toast({
        title: "Producto agregado",
        description: `${product.name} se ha aadido al carrito`,
      });
    } catch (error) {
      const items = cartStore.getItems();
      const itemToRemove = items.find(
        (item) => item.productId === productId && item.id.includes(`${productId}-`)
      );
      if (itemToRemove) cartStore.removeItem(itemToRemove.id);

      toast({
        title: "Error",
        description: "No se pudo agregar el producto al carrito. Intenta de nuevo.",
        variant: "destructive",
      });
    } finally {
      setIsAddingToCart(null);
    }
  };

  const addToWishlist = (product: any) => {
    toast({
      title: "Agregado a favoritos",
      description: `${product.name} se ha aadido a tu lista de deseos`,
    });
  };

  if (productsLoading) {
    return (
      <div className="min-h-screen bg-background">
        <div className="container mx-auto px-4 py-8">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[...Array(6)].map((_, i) => (
              <Card key={i} className="animate-pulse">
                <div className="aspect-square bg-muted rounded-t-lg"></div>
                <CardContent className="p-4">
                  <div className="h-4 bg-muted rounded mb-2"></div>
                  <div className="h-6 bg-muted rounded mb-2"></div>
                  <div className="h-16 bg-muted rounded"></div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <section className="bg-gradient-to-r from-cornsilk to-warm py-12">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl font-bold text-foreground mb-4">
            Catlogo de Productos
          </h1>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            Explora nuestra coleccin completa de cermicas artesanales. Cada
            pieza es nica y est hecha con amor y dedicacin.
          </p>
        </div>
      </section>

      <div className="container mx-auto px-4 py-8">
        {/* Filters */}
        <div className="bg-card rounded-lg p-6 mb-8 shadow-sm">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            {/* Search */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
              <Input
                placeholder="Buscar productos..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>

            {/* Category Select */}
            <Select
              value={selectedCategory}
              onValueChange={(value) => setSelectedCategory(value)}
            >
              <SelectTrigger>
                <SelectValue placeholder="Todas las categoras" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todas las categoras</SelectItem>
                {categoryList.map((category: any) => (
                  <SelectItem
                    key={category._id}
                    value={String(category._id)}
                  >
                    {category.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            {/* Sort */}
            <Select value={sortBy} onValueChange={(value) => setSortBy(value)}>
              <SelectTrigger>
                <SelectValue placeholder="Ordenar por" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="name">Nombre A-Z</SelectItem>
                <SelectItem value="price-low">Precio: Menor a Mayor</SelectItem>
                <SelectItem value="price-high">Precio: Mayor a Menor</SelectItem>
              </SelectContent>
            </Select>

            <Button
              variant="outline"
              className="border-ceramics text-ceramics hover:bg-ceramics hover:text-ceramics-foreground"
            >
              <Filter className="h-4 w-4 mr-2" />
              Ms Filtros
            </Button>
          </div>
        </div>

        <div className="mb-6">
          <p className="text-muted-foreground">
            Mostrando {filteredProducts.length} productos
            {searchTerm && ` para "${searchTerm}"`}
            {selectedCategory !== "all" &&
              ` en "${categoryList.find((cat) => String(cat._id) === selectedCategory)?.name ||
              selectedCategory
              }"`}
          </p>
        </div>

        {/* Product Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredProducts.map((product: any, index: number) => {
            const productId = product.idProduct || `temp-${index}`;
            const isLoading = isAddingToCart === productId;

            return (
              <Card
                key={productId}
                className="group cursor-pointer transition-all duration-500 hover:shadow-2xl hover:-translate-y-2 bg-gradient-to-b from-card to-card/50 border-0 shadow-lg overflow-hidden"
              >
                <CardHeader className="p-0">
                  <div className="aspect-[4/3] rounded-t-lg relative overflow-hidden">
                    <img
                      src={product.url || "https://via.placeholder.com/400x300?text=Sin+Imagen"}
                      alt={product.name}
                      className="w-full h-full object-cover"
                      onError={(e) => {
                        (e.target as HTMLImageElement).src =
                          "https://via.placeholder.com/400x300?text=Sin+Imagen";
                      }}
                    />
                    <div className="absolute top-4 left-4 flex flex-col gap-2">
                      <Badge className="bg-ceramics hover:bg-ceramics/90 text-ceramics-foreground shadow-lg">
                        Artesanal
                      </Badge>
                      {product.custom && (
                        <Badge
                          variant="secondary"
                          className="bg-white/90 text-foreground shadow-lg"
                        >
                          Personalizable
                        </Badge>
                      )}
                    </div>
                    <Button
                      variant="ghost"
                      size="icon"
                      className="absolute top-4 right-4 bg-white/90 hover:bg-white text-foreground shadow-lg backdrop-blur-sm"
                      onClick={(e) => {
                        e.stopPropagation();
                        addToWishlist(product);
                      }}
                    >
                      <Heart className="h-5 w-5" />
                    </Button>
                  </div>
                </CardHeader>
                <CardContent className="p-6">
                  <CardTitle className="text-xl mb-3 line-clamp-2 text-foreground font-semibold">
                    {product.name}
                  </CardTitle>
                  <CardDescription className="text-muted-foreground mb-4 line-clamp-3 leading-relaxed">
                    {product.description}
                  </CardDescription>
                  <div className="flex items-center justify-between mb-4">
                    <div className="flex flex-col">
                      <span className="text-2xl font-bold text-ceramics">
                        ${product.price}
                      </span>
                      <span className="text-sm text-muted-foreground">
                        Precio por unidad
                      </span>
                    </div>
                    <Badge
                      variant={
                        product.stock > 10
                          ? "default"
                          : product.stock > 0
                            ? "secondary"
                            : "destructive"
                      }
                      className="px-3 py-1"
                    >
                      {product.stock > 0 ? `${product.stock} disponibles` : "Agotado"}
                    </Badge>
                  </div>
                  <div className="text-sm text-muted-foreground mb-4 p-2 bg-muted/50 rounded-lg">
                    <span className="font-medium">Categora:</span>{" "}
                    {categoryList.find((cat) => String(cat._id) === String(product.category))?.name ||
                      "Cermica General"}
                  </div>
                </CardContent>
                <CardFooter className="p-6 pt-0 flex gap-3">
                  <Button
                    className="flex-1 bg-ceramics hover:bg-ceramics/90 text-ceramics-foreground h-12 text-base font-medium shadow-lg hover:shadow-xl transition-all duration-300"
                    disabled={product.stock === 0 || isAddingToCart === productId}
                    onClick={() => addToCart(product)}
                  >
                    {isAddingToCart === productId ? (
                      <>
                        <div className="h-5 w-5 mr-2 animate-spin rounded-full border-2 border-ceramics-foreground border-t-transparent"></div>
                        Agregando...
                      </>
                    ) : (
                      <>
                        <ShoppingCart className="h-5 w-5 mr-2" />
                        Agregar al Carrito
                      </>
                    )}
                  </Button>
                  <Button
                    variant="outline"
                    size="icon"
                    className="h-12 w-12 border-ceramics text-ceramics hover:bg-ceramics hover:text-ceramics-foreground"
                    onClick={() => addToWishlist(product)}
                  >
                    <Heart className="h-5 w-5" />
                  </Button>
                </CardFooter>

              </Card>
            );
          })}
        </div>

        {filteredProducts.length === 0 && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4"></div>
            <h3 className="text-xl font-semibold mb-2">No se encontraron productos</h3>
            <p className="text-muted-foreground">Intenta ajustar tus filtros de bsqueda</p>
          </div>
        )}
      </div>
    </div>
  );
}

import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { customerAPI, getCurrentUser } from "@/lib/api";

export default function Profile() {
  const { toast } = useToast();
  const [isSaving, setIsSaving] = useState(false);
  const [user, setUser] = useState<any | null>(null);
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    billingAddress: "",
    shippingAddress: "",
  });

  useEffect(() => {
    document.title = "Perfil del cliente | Barroco Ceramics";
    const current = getCurrentUser();
    setUser(current);
    if (current) {
      setForm({
        firstName: current.firstName || "",
        lastName: current.lastName || "",
        email: current.email || "",
        phone: current.phone || "",
        billingAddress: current.billingAddress || "",
        shippingAddress: current.shippingAddress || "",
      });
    }
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!user?._id) return;
    setIsSaving(true);
    try {
      const updatePayload = { ...form } as any;
      delete updatePayload.password;
      const updated = await customerAPI.update(user._id, updatePayload);

      const newUser = { ...user, ...updatePayload, ...(typeof updated === "object" ? updated : {}) };
      sessionStorage.setItem("user", JSON.stringify(newUser));
      setUser(newUser);

      toast({ title: "Perfil actualizado", description: "Tus datos se guardaron correctamente." });
    } catch (err) {
      toast({ title: "Error", description: "No se pudo guardar el perfil.", variant: "destructive" });
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <div className="container mx-auto px-6 py-10">
      <header className="mb-8 text-center">
        <h1 className="text-3xl font-bold text-gray-800">Mi perfil</h1>
        <p className="text-gray-500 text-sm mt-2">
          Actualiza tu informacin personal y de envo.
        </p>
      </header>

      <Card className="max-w-5xl mx-auto rounded-2xl shadow-md border border-gray-200 bg-[#ececec]">
        <CardHeader className="pb-4 border-b border-gray-200">
          <CardTitle className="text-lg font-semibold text-gray-800">
            Informacin del cliente
          </CardTitle>
        </CardHeader>

        <CardContent className="pt-6">
          <form onSubmit={handleSubmit} className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            <div className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="firstName" className="text-gray-700 font-medium">Nombre</Label>
                <Input
                  id="firstName"
                  value={form.firstName}
                  onChange={(e) => setForm((p) => ({ ...p, firstName: e.target.value }))}
                  className="rounded-xl border-gray-300 focus:border-[#E7A97C] focus:ring-[#E7A97C]"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="lastName" className="text-gray-700 font-medium">Apellido</Label>
                <Input
                  id="lastName"
                  value={form.lastName}
                  onChange={(e) => setForm((p) => ({ ...p, lastName: e.target.value }))}
                  className="rounded-xl border-gray-300 focus:border-[#E7A97C] focus:ring-[#E7A97C]"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="email" className="text-gray-700 font-medium">Email</Label>
                <Input
                  id="email"
                  type="email"
                  value={form.email}
                  onChange={(e) => setForm((p) => ({ ...p, email: e.target.value }))}
                  className="rounded-xl border-gray-300 focus:border-[#E7A97C] focus:ring-[#E7A97C]"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="phone" className="text-gray-700 font-medium">Telfono</Label>
                <Input
                  id="phone"
                  value={form.phone}
                  onChange={(e) => setForm((p) => ({ ...p, phone: e.target.value }))}
                  className="rounded-xl border-gray-300 focus:border-[#E7A97C] focus:ring-[#E7A97C]"
                />
              </div>
            </div>

            <div className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="billingAddress" className="text-gray-700 font-medium">
                  Direccin de facturacin
                </Label>
                <Input
                  id="billingAddress"
                  value={form.billingAddress}
                  onChange={(e) => setForm((p) => ({ ...p, billingAddress: e.target.value }))}
                  className="rounded-xl border-gray-300 focus:border-[#E7A97C] focus:ring-[#E7A97C]"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="shippingAddress" className="text-gray-700 font-medium">
                  Direccin de envo
                </Label>
                <Input
                  id="shippingAddress"
                  value={form.shippingAddress}
                  onChange={(e) => setForm((p) => ({ ...p, shippingAddress: e.target.value }))}
                  className="rounded-xl border-gray-300 focus:border-[#E7A97C] focus:ring-[#E7A97C]"
                />
              </div>

              <div className="flex justify-center pt-6">
                <Button
                  type="submit"
                  disabled={isSaving}
                  className="bg-[#E7A97C] hover:bg-[#d98a60] text-white rounded-xl px-6 py-2"
                >
                  {isSaving ? "Guardando..." : "Guardar cambios"}
                </Button>
              </div>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );


}

import '@testing-library/jest-dom';
import { vi } from 'vitest';

// Shim para jest
(globalThis as any).jest = {
  fn: vi.fn,
  mock: vi.mock,
  spyOn: vi.spyOn,
  clearAllMocks: vi.clearAllMocks,
  requireActual: vi.importActual,
